import firebase from "firebase/app";
import 'firebase/auth';
import 'firebase/firestore';
import config from "./config";

// Initialize Firebase y lo metemos en una constatante
 const fb = firebase.initializeApp(config.firebase);

 //Utilizar el metodo de firestore y guaradar en una constate para poder exportarlo:
 //Este metodo es para guardar colecciones, guardar datos.. etc
 export const db = fb.firestore();

 export default db;
